// Category Row component - matches ElementsPanel style
function IconCategoryRow({
    category,
    icons,
    isExpanded,
    onToggle,
    onAddIcon,
}: {
    category: { id: string; name: string };
    icons: { id: string; name: string; path: string }[];
    isExpanded: boolean;
    onToggle: () => void;
    onAddIcon: (icon: { id: string; name: string; path: string }) => void;
}) {
    const scrollContainerRef = useRef<HTMLDivElement>(null);

    const scrollRight = () => {
        if (scrollContainerRef.current) {
            scrollContainerRef.current.scrollBy({ left: 200, behavior: 'smooth' });
        }
    };

    return (
        <div className="mb-4">
            {/* Category Header */}
            <div className="flex items-center justify-between mb-2">
                <button
                    onClick={onToggle}
                    className="flex items-center gap-2 text-gray-700 hover:text-gray-900"
                >
                    {isExpanded ? (
                        <ChevronDown size={14} />
                    ) : (
                        <ChevronRight size={14} />
                    )}
                    <h3 className="text-sm font-semibold">
                        {category.name} ({icons.length})
                    </h3>
                </button>
                {/* Right scroll arrow - only show when collapsed and has more than 4 icons */}
                {!isExpanded && icons.length > 4 && (
                    <button
                        onClick={scrollRight}
                        className="p-1 text-gray-500 hover:text-gray-800 transition-colors"
                        title="Scroll right"
                    >
                        <ChevronRight size={18} />
                    </button>
                )}
            </div>

            {/* Collapsed: Horizontal Scrollable Row */}
            {!isExpanded && (
                <div
                    ref={scrollContainerRef}
                    className="flex gap-2 overflow-x-auto pb-2 scrollbar-hide"
                    style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
                >
                    {icons.map((icon) => (
                        <button
                            key={icon.id}
                            onClick={() => onAddIcon(icon)}
                            className="flex-shrink-0 w-16 h-16 rounded-lg hover:bg-blue-50 cursor-pointer transition-all duration-200 flex items-center justify-center text-gray-700 hover:text-blue-600"
                            title={icon.name}
                        >
                            <svg
                                viewBox="0 0 24 24"
                                className="w-10 h-10"
                                fill="currentColor"
                            >
                                <path d={icon.path} />
                            </svg>
                        </button>
                    ))}
                </div>
            )}

            {/* Expanded: Full Grid */}
            {isExpanded && (
                <div className="grid grid-cols-4 gap-2">
                    {icons.map((icon) => (
                        <button
                            key={icon.id}
                            onClick={() => onAddIcon(icon)}
                            className="w-16 h-16 rounded-lg hover:bg-blue-50 cursor-pointer transition-all duration-200 flex items-center justify-center text-gray-700 hover:text-blue-600"
                            title={icon.name}
                        >
                            <svg
                                viewBox="0 0 24 24"
                                className="w-10 h-10"
                                fill="currentColor"
                            >
                                <path d={icon.path} />
                            </svg>
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
}

interface IconsPanelProps {
    searchQuery?: string;
}

export function IconsPanel({ searchQuery = '' }: IconsPanelProps) {
    const [expandedCategories, setExpandedCategories] = useState<Set<string>>(new Set());
    const addShapeElement = useCanvasStore((state) => state.addShapeElement);

    const toggleCategory = (categoryId: string) => {
        setExpandedCategories(prev => {
            const newSet = new Set(prev);
            if (newSet.has(categoryId)) {
                newSet.delete(categoryId);
            } else {
                newSet.add(categoryId);
            }
            return newSet;
        });
    };

    // Filter icons based on search query
    const filteredCategories = useMemo(() => {
        if (!searchQuery.trim()) return ICON_CATEGORIES;

        const query = searchQuery.toLowerCase();
        return ICON_CATEGORIES.map(category => ({
            ...category,
            icons: category.icons.filter(icon =>
                icon.name.toLowerCase().includes(query) ||
                icon.id.toLowerCase().includes(query) ||
                category.name.toLowerCase().includes(query)
            ),
        })).filter(category => category.icons.length > 0);
    }, [searchQuery]);

    // Add icon to canvas using store (integrates with layers, toolbar, sidebar)
    const handleAddIcon = (icon: { id: string; name: string; path: string }) => {
        // Get canvas dimensions to center the icon
        const fabricCanvas = getFabricCanvas();
        const canvas = fabricCanvas.getCanvas();
        if (!canvas) return;

        const canvasWidth = canvas.getWidth() || 1080;
        const canvasHeight = canvas.getHeight() || 1080;

        // Use addShapeElement from store with svgPath
        addShapeElement(`icon-${icon.id}`, {
            name: `Icon (${icon.name})`,
            svgPath: icon.path,
            transform: {
                x: canvasWidth / 2,
                y: canvasHeight / 2,
                width: 100,
                height: 100,
                scaleX: 1,
                scaleY: 1,
                rotation: 0,
                skewX: 0,
                skewY: 0,
                originX: 'center',
                originY: 'center',
            },
            style: {
                fill: '#1a1a1a',
                stroke: null,
                strokeWidth: 0,
                opacity: 1,
                shadow: null,
                cornerRadius: 0,
            },
        });
    };

    return (
        <div className="h-full flex flex-col bg-white">
            {/* Scrollable Content */}
            <div className="flex-1 overflow-y-auto p-4 custom-scrollbar">
                {filteredCategories.map((category) => (
                    <IconCategoryRow
                        key={category.id}
                        category={category}
                        icons={category.icons}
                        isExpanded={expandedCategories.has(category.id)}
                        onToggle={() => toggleCategory(category.id)}
                        onAddIcon={handleAddIcon}
                    />
                ))}

                {/* Empty state */}
                {filteredCategories.length === 0 && (
                    <div className="flex flex-col items-center justify-center py-12 text-gray-400">
                        <Search size={32} strokeWidth={1} />
                        <p className="mt-3 text-sm">No icons found</p>
                    </div>
                )}
            </div>
        </div>
    );
}

